var iframe = document.getElementById('iframe')
    var rIframe = document.getElementById('rIframe')
    iframe.onload = function(){
        iframe.style.height = rIframe.style.height = (iframe.contentWindow.document.body.offsetHeight + 50) + 'px'
    }





    .rIframe {
        position: relative;
        height: 0;
        overflow: hidden;
        /*padding-bottom: 75%;*/
    }

    .rIframe iframe {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
    }

    .md-content__inner h1 {
        display: none;
    }
